mapper.update:
UPDATE student_demand_connect
SET delete_status = 1
WHERE id =  #{id}


    SELECT @@IDENTITY AS sid

UPDATE student_demand_connect
SET order_teach_time = #{confirmDate},
    status  = #{status}
Where demand_id = #{demandId}
AND teacher_id = #{teacherId}

UPDATE student_demand_connect
SET status = #{status},
update_time  = #{updateTime}
Where demand_id = #{demandId}
AND teacher_id != #{teacherId}

mapper.select:
SELECT * FROM student_demand_connect
ORDER BY id DESC
limit #{pageSize} offset #{offset}

        SELECT count(*)
        FROM student_demand_connect sdc
		INNER JOIN student_demand sd
		ON sd.sid = sdc.demand_id
        WHERE sdc.delete_status = 0
		AND sdc.teacher_id =  #{teacherId}
		AND sdc.status = 4
		AND sd.demand_grade = #{demandGrade}
		AND sd.subject_id   = #{subjectId}
		AND sd.student_id = #{studentId}

SELECT sd.sid, sdc.demand_id, t.`name` as teacherName, t.telephone, s.student_name, s.parent_phone_num, sd.create_time, tg.teach_grade_name, tb.teach_branch_name, sd.time_range
FROM student_demand_connect sdc LEFT JOIN student_demand sd ON sdc.demand_id = sd.sid LEFT JOIN teach_grade tg ON sd.demand_grade = tg.teach_grade_id LEFT JOIN teach_branch tb ON sd.subject_id = tb.teach_branch_id LEFT JOIN student s ON sd.student_id = s.sid LEFT JOIN teacher t ON sdc.teacher_id = t.teacher_id WHERE sdc.status = 1 AND sdc.is_contact = 0 AND sdc.order_teach_time is NULL AND sdc.create_time < #{createTime}
ORDER BY sd.create_time asc
limit #{pageSize} offset #{offset}
