mapper.select:
    
        select
        	sd.*
		from
			student_demand sd
		where
			1=1
		
            AND sd.sid = #{sid}
        
        
            AND sd.demand_grade = #{demandGrade}
         
        
            AND sd.demand_branch = #{demandBranch}
         
        
            AND sd.student_area = #{studentArea}
         

        order by 
        	sd.create_time desc
        
            
                limit #{pageSize} offset #{offset}
