mapper.select:
select tpl.payment_id, tpl.payment_stream_id, tpl.payment_person_id, t.telephone, tpl.payment_person_name, tpl.payment_type, tpl.payment_account, tpl.payment_desc, tpl.create_time FROM teacher_payments_log tpl left join teacher t ON tpl.payment_person_id = t.teacher_id WHERE tpl.status =1 AND tpl.payment_type = 1 AND tpl.payment_account > 800
ORDER BY tpl.create_time desc
limit #{pageSize} offset #{offset}
